/*DROP TABLE PRIJAVAMOB;DROP TABLE PODRUCJE;DROP TABLE STUDENT;DROP TABLE INSTITUCIJA;DROP TABLE MJESTO;DROP TABLE DRZAVA;DROP TABLE AKGODINA;DROP TABLE RAZINASTUDIJ;DROP TABLE VRINSTITUCIJA;*/CREATE TABLE VRINSTITUCIJA(  sifVrInstitucija INT NOT NULL,  nazVrInstitucija VARCHAR(200) NOT NULL,  PRIMARY KEY (sifVrInstitucija),  UNIQUE (nazVrInstitucija));CREATE TABLE RAZINASTUDIJ(  sifRazinaStudij INT NOT NULL,  nazRazinaStudij VARCHAR(100) NOT NULL,  PRIMARY KEY (sifRazinaStudij),  UNIQUE (nazRazinaStudij));CREATE TABLE AKGODINA(  sifAkGodina INT NOT NULL,  opisAkGodina VARCHAR(100) NOT NULL,  PRIMARY KEY (sifAkGodina),  UNIQUE (opisAkGodina));CREATE TABLE DRZAVA(  ISOOznaka CHAR(2) NOT NULL,  nazDrzava VARCHAR(100) NOT NULL,  PRIMARY KEY (ISOOznaka),  UNIQUE (nazDrzava));CREATE TABLE MJESTO(  sifMjesto INT NOT NULL,  postOzn INT,  nazMjesto VARCHAR(100) NOT NULL,  ISOOznaka CHAR(2) NOT NULL,  PRIMARY KEY (sifMjesto),  FOREIGN KEY (ISOOznaka) REFERENCES DRZAVA(ISOOznaka));CREATE TABLE INSTITUCIJA(  sifInstitucija INT NOT NULL,  nazInstitucija VARCHAR(200) NOT NULL,  URIInstitucija VARCHAR(100) NOT NULL,  sifVrInstitucija INT NOT NULL,  sifMjesto INT NOT NULL,  PRIMARY KEY (sifInstitucija),  FOREIGN KEY (sifVrInstitucija) REFERENCES VRINSTITUCIJA(sifVrInstitucija),  FOREIGN KEY (sifMjesto) REFERENCES MJESTO(sifMjesto),  UNIQUE (URIInstitucija));CREATE TABLE STUDENT(  JMBAG CHAR(10) NOT NULL,  imeS VARCHAR(25) NOT NULL,  prezS VARCHAR(25) NOT NULL,  datRod DATE NOT NULL,  spol INT NOT NULL,  eMail VARCHAR(50) NOT NULL,  sifInstitucija INT NOT NULL,  PRIMARY KEY (JMBAG),  FOREIGN KEY (sifInstitucija) REFERENCES INSTITUCIJA(sifInstitucija),  UNIQUE (eMail));CREATE TABLE PODRUCJE(  sifPodrucje INT NOT NULL,  nazPodrucje VARCHAR(200) NOT NULL,  nadPod_sifPodrucje INT,  PRIMARY KEY (sifPodrucje),  FOREIGN KEY (nadPod_sifPodrucje) REFERENCES PODRUCJE(sifPodrucje),  UNIQUE (nazPodrucje));CREATE TABLE PRIJAVAMOB(  prioritet INT NOT NULL CONSTRAINT checkPrioritet CHECK(prioritet BETWEEN 1 AND 3),  semestar INT NOT NULL,  datPocMob DATE NOT NULL,  datKrajMob DATE NOT NULL,  JMBAG CHAR(10) NOT NULL,  sifAkGodina INT NOT NULL,  sifInstitucija INT NOT NULL,  sifRazinaStudij INT NOT NULL,  sifPodrucje INT NOT NULL,  PRIMARY KEY (JMBAG, sifAkGodina, sifInstitucija),  FOREIGN KEY (JMBAG) REFERENCES STUDENT(JMBAG),  FOREIGN KEY (sifAkGodina) REFERENCES AKGODINA(sifAkGodina),  FOREIGN KEY (sifInstitucija) REFERENCES INSTITUCIJA(sifInstitucija),  FOREIGN KEY (sifRazinaStudij) REFERENCES RAZINASTUDIJ(sifRazinaStudij),  FOREIGN KEY (sifPodrucje) REFERENCES PODRUCJE(sifPodrucje),  CONSTRAINT uniquePrijava UNIQUE (JMBAG,sifAkGodina, prioritet),  CONSTRAINT chkTrajanjeMobilnost CHECK ((datKrajMob >= datPocMob + '3 months'::INTERVAL) AND (datKrajMob < datPocMob + '12 months'::INTERVAL)));